typeProperties.script:
section Section1;
shared FLOWLETINPUT = let AdfDoc = AzureStorage.BlobContents("https://stganandjun23.blob.core.windows.net/flowlets/Input/emp_sal.csv"),Csv = Csv.Document(AdfDoc, [Delimiter = ",", Encoding = TextEncoding.Utf8, QuoteStyle = QuoteStyle.Csv]), PromotedHeaders = Table.PromoteHeaders(Csv, [PromoteAllScalars = true]) in  PromotedHeaders;
shared UserQuery = let Source = #"FLOWLETINPUT",
  #"Changed column type" = Table.TransformColumnTypes(Source, {{"Salary", Int64.Type}}),
  #"Filtered rows" = Table.SelectRows(#"Changed column type", each [Salary] > 3000),
  #"Grouped rows" = Table.Group(#"Filtered rows", {"country_id", "department_name"}, {{"Sum_Salary", each List.Sum([Salary]), type nullable number}}) in #"Grouped rows";
